INSERT INTO `wx_blog` VALUES (20220317130001, 'python çˆ¬å–å›¾ç‰‡æµ‹è¯•', '<h2>pythonçˆ¬å–å›¾ç‰‡å¤šçº¿ç¨‹</h2>\n<p>çˆ¬ç€ç©çš„å“ˆ</p>\n<p>å¯ä»¥å½“å£çº¸å“¦ï¼</p>\n<p>æ‰“åŒ…å¥½çš„exeæ–‡ä»¶</p>\n<p>ä¸‹è½½:<a href=\"https://wwe.lanzous.com/iOmivox45cd\">ğŸ”—</a> å¯†ç :4iwj</p>\n<pre><code class=\"language-python\">&quot;&quot;&quot;ä½¿ç”¨å¤šçº¿ç¨‹ä¸‹è½½æ•°æ® æœ¬æ¬¡æµ‹è¯• å½¼å²¸å›¾ç½‘ &quot;&quot;&quot;\nimport os\nimport requests\nimport threading\nimport time\nimport random\nfrom bs4 import BeautifulSoup\nfrom fake_useragent import UserAgent\n\n# åˆ›å»ºæ–‡ä»¶å¤¹\ndef create_path():\n    path = os.getcwd() + &quot;\\\\å½¼å²¸å›¾&quot;\n    if not os.path.exists(path):\n        os.mkdir(path)\n    return path\n\n\n# æå–è§£æä¹‹åçš„responseæ•°æ®\ndef parse_data(link):\n    try:\n        time.sleep(random.random() * 3)  # æ·»åŠ ä¼‘çœ æ—¶é—´ï¼Œé˜²æ­¢è®¿é—®æœåŠ¡å™¨è¢«å°\n        data = requests.get(url=link, headers=random_headers()).content.decode(&quot;gbk&quot;)\n        html = BeautifulSoup(data, \'lxml\')\n        return html\n    except:\n        pass\n\n\n# æå–æ•°æ®\ndef get_imgList(data):\n    # è·å–å›¾ç‰‡é“¾æ¥åœ°å€\n    img_list = data.find(\'div\', {\'class\': \'slist\'}, \'li\').find_all(&quot;a&quot;)\n    for i, img in enumerate(img_list):\n        try:\n            # å¤§å›¾ç‰‡çš„åœ°å€url\n            if img.get(&quot;href&quot;) is not None:\n                img_url = root_url + img.get(&quot;href&quot;)\n                # è®¿é—®å¤§å›¾ç‰‡åœ°å€\n                jgp_html_data = parse_data(img_url)\n                # è·å–jpgæ ¼å¼çš„é“¾æ¥åœ°å€\n                j_ = jgp_html_data.find(&quot;img&quot;).get(&quot;src&quot;)\n                # å½“è·å–ä¸åˆ°urlæ—¶ä¸å‚ä¸æ‰§è¡Œ\n                if j_ is not None:\n                    jpg_url = root_url + j_\n                    jpg_name = jgp_html_data.find(\'img\').get(\'title\')\n                    # å¤šçº¿ç¨‹ä¸‹è½½å›¾ç‰‡\n                    if jpg_name is not None:\n                        t1 = threading.Thread(target=sa