INSERT INTO `wx_blog` VALUES (1527100878353395712, 'è±†ç“£è¯„åˆ†top250', '<h3>ä½¿ç”¨åº“</h3>\n<pre><code class=\"language-shell\">### è·å–è¯·æ±‚å“åº”\npip install requrest\n### ä¼ªé€ è¯·æ±‚å¤´\npip install fake_useragent\n### bs4 é¡µé¢å†…å®¹è§£æ\npip install bs4\n### excel åº“\npip install xlwt\n</code></pre>\n<h3>ä»£ç </h3>\n<pre><code class=\"language-python\">import requests\nfrom bs4 import BeautifulSoup\nimport xlwt\nimport time\nfrom fake_useragent import UserAgent\nimport os\n\n\ndef fake_header():\n    \'\'\'\n    ä¼ªé€ è¯·æ±‚å¤´ æˆ¿å­ipè¢«å°\n    :return: è¯·æ±‚å¤´\n    \'\'\'\n    return {\n        \'user-agent\': UserAgent().chrome\n    }\n\n\ndef resp(url):\n    \'\'\'\n    è·å–è¯·æ±‚ä¿¡æ¯\n    :param url: è¯·æ±‚url\n    :return: success:html error:None\n    \'\'\'\n    # ä¼‘çœ åç§’ä¸­ é˜²æ­¢ipè¢«å°\n    time.sleep(10)\n    response = requests.get(url=url, headers=fake_header())\n    if response.status_code == 200:\n        response.encoding = response.apparent_encoding\n        return response.text\n    else:\n        print(&quot;è¯·æ±‚å¤±è´¥ï¼ï¼ï¼&quot;)\n        return None\n\n\ndef parseData(url):\n    \'\'\'\n    è§£ææ•°æ®\n    :param url: åœ°å€\n    :return: è§£æä¹‹åæ•°æ®\n    \'\'\'\n    data = resp(url)\n    # print(data)\n    soup = BeautifulSoup(data, \'html.parser\')\n    items = soup.find_all(\'div\', {\'class\': \'item\'})\n    # print(len(items))\n    for (index, item) in enumerate(items):\n        try:\n            # åç§°\n            movie_name = item.select_one(\'div.info span.title\').get_text()\n            # åœ°å€\n            movie_url = item.select_one(\'div\', {\'class\': \'info\'}).a[\'href\']\n            # å…¶ä»–\n            movie_other_info = item.select_one(\'div.info span.other\').get_text()\n            # è¯„åˆ†\n            movie_star = item.select_one(\'div.info div.bd div.star span.rating_num\').get_text()\n            # è¯„ä»·äººæ•°\n            movie_star_users = item.select_one(\'div.info div.bd div.star span:last-child\').get_text()\n            # æ¥æºä¿¡æ¯\n            movie_sources = item.select_one(\'div.info div.bd p\', {\'class\': &quot;&quot;}).get_text()\n            # ç”µå½±åè¨€\n            movie_saying = item.select_one(\'div.info div.bd p.quote\').get_text()\n            # print(movie_name,movie_url,movie_other_info,movie_star,movie_star_users,movie_sources,movie_saying)\n            data = [movie_name, movie_url, movie_other_info, movie_star, movie_star_users, movie_sources, movie_saying]\n            # å­˜æ”¾æ¯ä¸€æ¡æ•°æ®\n            data_list.append(data)\n        except Exception as e:\n            print(\'å¼‚å¸¸ä¿¡æ¯\', e)\n\n\ndef run():\n    \'\'\'\n    å¼€å§‹å‡½æ•°\n    :return:\n    \'\'\'\n    for i in range(0, 10):\n        print(\'æ­£åœ¨è·å–ç¬¬{}é¡µæ•°æ®\'.format(i + 1))\n        url = \'https://movie.douban.com/top250?start={}&amp;filter=\'.format(i * 25)\n        parseData(url)\n    book = xlwt.Workbook(encoding=\'utf-8\', style_compression=0)\n    sheet = book.add_sheet(\'è±†ç“£ç”µå½±Top250\', cell_overwrite_ok=True)\n    # å†…å®¹å†™å…¥\n    col = (\'åç§°\', \'åœ°å€\', \'å…¶ä»–\', \'è¯„åˆ†\', \'è¯„ä»·äººæ•°\', \'æ¥æº\', \'åè¨€\')\n    # sheet.write(\'row\',\'col\',\'data\') # è¡Œ åˆ— æ•°æ®\n    for m in range(0, len(col) - 1):\n        sheet.write(0, m, col[m])\n    # éå†data_list\n    for (index, item) in enumerate(data_list):\n        # éå†æ¯ä¸€æ¡ï¼Œå†™å…¥\n        for n in range(0, len(item) - 1):\n            sheet.write(index + 1, n, item[n])\n            # ä¿å­˜è·¯å¾„\n            savepath = \'è±†ç“£ç”µå½±Top250.xls\'\n            book.save(savepath)\n\n\nif __name__ == \'__main__\':\n    # å­˜æ”¾æ¯ä¸€æ¡ç”µå½±\n    data_list = []\n    run()\n    print(\'æ–‡ä»¶ä¿å­˜ä½ç½®----{}\'.format(os.getcwd()+\'\\è±†ç“£ç”µå½±Top250.xls\'))\n</code></pre>\n', '<p><img src=\"https://fastly.jsdelivr.net/gh/wuxin0011/wuxin@main/img/202205/20220519013019.png\" alt=\"202205/20220519013019.png\" /></p>\n', '0', '1', '2022-05-19 09:36:36', NULL, NULL, '0', '', 1, 35,